function equals(c,n){return OPS[calc.currentOp](Number(c),Number(n))}function screenVal(c){$(".screen").text(c)}function reset(c){calc.valA=c,calc.valB="",calc.currentVal="valA"}const OPS={plus:function(c,n){return c+n},minus:function(c,n){return c-n},multiply:function(c,n){return c*n},divide:function(c,n){return c/n}};var calc={currentOp:"",currentVal:"valA",valA:"",valB:""};$(".num-pad").click(function(c){calc[calc.currentVal]+=c.target.innerText,screenVal(calc[calc.currentVal])}),$(".basic-ops").click(function(c){calc.currentOp=c.target.id,calc.currentVal="valB"}),$(".ac-btn").click(function(){screenVal("0"),reset("")}),$(".equals-btn").click(function(){var c=equals(calc.valA,calc.valB);screenVal(c),reset(c)});