function equals(c,n){return OPS[calc.operator](Number(c),Number(n))}function updater(c){calc[calc.current]+=c,screenVal(calc[calc.current])}function negator(c){return c=-1===c.indexOf("-")&&"0"!==c?"-"+c:c.replace("-",""),screenVal(c),c}function screenVal(c){$(".screen").text(c)}function reset(c){calc.valA=c,calc.valB="",calc.current="valA"}const OPS={plus:function(c,n){return c+n},minus:function(c,n){return c-n},multiply:function(c,n){return c*n},divide:function(c,n){return c/n}};var calc={operator:"",current:"valA",valA:"",valB:""};$(".basic-ops").click(function(c){calc.operator=c.target.id,calc.current="valB"}),$(".num-pad").click(function(c){updater(c.target.innerText)}),$(".negate-btn").click(function(){calc[calc.current]=negator(String(calc[calc.current]))}),$(".decimal-btn").click(function(){-1===calc[calc.current].indexOf(".")&&updater(".")}),$(".ac-btn").click(function(){screenVal("0"),reset("")}),$(".equals-btn").click(function(){var c=equals(calc.valA,calc.valB);screenVal(c),reset(c)});